mapper.update:
UPDATE coupon SET


		chkusable = 'Y' WHERE coupon_code = #{couponCode}


		chkusable = 'Y', userid = #{userid} WHERE coupon_code = #{couponCode}

mapper.select:
SELECT COUNT(*) FROM coupon


		WHERE userid = #{userid} 
		AND chkusable = 'N' 
		AND isPresent = 'Y'


		WHERE coupon_code = #{couponCode}
		AND chkusable = 'N'
